import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import ImageComponent from './ImageComponent/ImageComponent';
import ArrowComponent from './ArrowComponent/ArrowComponent.js'

class App extends Component {

  state = {
    images: ['slide-1.jpg',
             'slide-2.jpg', 
             'slide-3.jpg', 
             'slide-4.jpg', 
             'slide-5.jpg', 
             'slide-6.jpg'],
    imageIndex: 0
  }

  setImageIndex = (index) => {
  }

  leftArrowClickHandler = () => {
    const lastIndexOfImages = this.state.images.length - 1;
    const imageIndex = this.state.imageIndex;
    const newImageIndex = (imageIndex === 0) ? lastIndexOfImages : imageIndex - 1;
    this.setImageIndex(newImageIndex);
  }

  rightArrowClickHandler = () => {
    const lastIndexOfImages = this.state.images.length - 1;
    const imageIndex = this.state.imageIndex;
    const newImageIndex = (imageIndex === lastIndexOfImages) ? 0 : imageIndex + 1;
    this.setState({index: newImageIndex});

    //this.setImageIndex(newImageIndex);
  }

  render() {

    // Grabs all images in list
    // const images = (
    //   <div>[]
    //     {this.state.images.map(imageName => {
    //       return <ImageComponent onClick={this.imageClickHandler} image={process.env.PUBLIC_URL + '/images/' + imageName}/>
    //     })}
    //   </div>
    // );


    return (
      <div className="App">
        <p>Hello world</p>
        <p>{this.state.images}</p>
        <ArrowComponent click={this.leftArrowClickHandler}></ArrowComponent>
        <ImageComponent image={process.env.PUBLIC_URL + '/images/' + this.state.images[this.state.imageIndex]}/>
        <ArrowComponent click={this.rightArrowClickHandler}></ArrowComponent>
      </div>
    );
  }
}

export default App;
