import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import ImageComponent from './ImageComponent/ImageComponent';

class App extends Component {

  state = {
    images: ['slide-1.jpg',
             'slide-2.jpg', 
             'slide-3.jpg', 
             'slide-4.jpg', 
             'slide-5.jpg', 
             'slide-6.jpg'],
    counter: 0
  }

  imageClickHandler = () => {
    this.setState({counter: this.state.counter+1});
  }

  render() {

    const images = (
      <div>[]
        {this.state.images.map(imageName => {
          return <ImageComponent onClick={this.imageClickHandler} image={process.env.PUBLIC_URL + '/images/' + this.state.images[this.state.counter]}/>
        })}
      </div>
    );




    return (
      <div className="App">
        <p>Hello world</p>
        <p>{this.state.images}</p>
        {images}
        <ImageComponent click={this.imageClickHandler} 
                        image={process.env.PUBLIC_URL + '/images/' + this.state.images[this.state.counter]}/>
      </div>
    );
  }
}

export default App;
